    path: '/Exam03',
    component: Exam03
  }, {
    path: '/ExamContainer04',
    component: ExamContainer04
  }, {
    path: '/ExamContainer05',
    component: ExamContainer05
  }, {
    path: '/ExamContainer06',
    component: ExamContainer06
  }, {
    path: '/Fa08',
    component: Fa08
  }]
});
