export default {
  data: function data() {
    //当前组建共享数据，模块直接读取
    return {//默认返回空对象（没数据）
    };
  },
  methods: {
    //功能，此函数完成显示mint-ui短消息
    openToast: function openToast() {
      //提示框
      this.$toast({
        message: "操作成功",
        //提示文字
        position: "top",
        //提示文字位置顶端
        duration: 1000,
        //消息提示框持续时间5秒
        //使用字体名称 图标名称（首页）
        iconClass: "iconfont icon-home"
      });
      console.log(this);
    },
    openAlert: function openAlert() {
      // 交互提示框

      /* 
      // 1标准方式显示提示框
      this.$messagebox({
        title:"消息",           //标题
        message:"执行操作成功"   //内容
      });
      */
      //以下简写
      this.$messagebox("消息", "执行操作成功");
    },
    openConfirm: function openConfirm() {
      //功能 ：显示确认消息提示框
      this.$messagebox.confirm("是否删除指定数据") //消息内容
      .then(function (action) {
        //用户选择确认回调函数
        console.log("确认");
      }).catch(function (err) {
        //用户选择取消回调函数
        console.log("取消");
      });
    },
    openPrompt: function openPrompt() {
      this.$messagebox.prompt("请输入年龄") //输入框
      .then(function (value) {
        //回调函数
        console.log(value); //输入数值
      }).catch(function (err) {
        //取消函数
        console.log(err); //取消
      });
    }
  }
};
